!function(){new Vue({el:"#main",data:{todos:[{completed:!0,text:"one"},{completed:!1,text:"two"},{completed:!0,text:"three"}]},methods:{removeTodo:function(a){this.todos.$remove(a.$data)},editTodo:function(a){a.editing=!0;var b=$(a.$el).find("input[type=text]");b.val(a.text),setTimeout(function(){b.focus()},10)},completeEditTodo:function(a){a.editing=!1;var b=$(a.$el).find("input[type=text]");a.text=b.val()||""}}})}(this);